import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { 
  insertWorkoutSchema, 
  insertClientSchema, 
  insertCoachProfileSchema 
} from "@shared/schema";

export async function registerRoutes(app: Express): Promise<Server> {
  // Workout routes
  app.get("/api/workouts", async (req, res) => {
    try {
      const workouts = await storage.getAllWorkouts();
      res.json(workouts);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch workouts" });
    }
  });

  app.get("/api/workouts/:id", async (req, res) => {
    try {
      const workout = await storage.getWorkout(req.params.id);
      if (!workout) {
        return res.status(404).json({ message: "Workout not found" });
      }
      res.json(workout);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch workout" });
    }
  });

  app.post("/api/workouts", async (req, res) => {
    try {
      const validatedData = insertWorkoutSchema.parse(req.body);
      const workout = await storage.createWorkout(validatedData);
      res.status(201).json(workout);
    } catch (error) {
      if (error && typeof error === 'object' && 'name' in error && error.name === 'ZodError') {
        return res.status(400).json({ message: "Validation error", details: error });
      }
      res.status(500).json({ message: "Failed to create workout" });
    }
  });

  app.put("/api/workouts/:id", async (req, res) => {
    try {
      const validatedData = insertWorkoutSchema.partial().parse(req.body);
      const workout = await storage.updateWorkout(req.params.id, validatedData);
      if (!workout) {
        return res.status(404).json({ message: "Workout not found" });
      }
      res.json(workout);
    } catch (error) {
      if (error && typeof error === 'object' && 'name' in error && error.name === 'ZodError') {
        return res.status(400).json({ message: "Validation error", details: error });
      }
      res.status(500).json({ message: "Failed to update workout" });
    }
  });

  app.delete("/api/workouts/:id", async (req, res) => {
    try {
      const deleted = await storage.deleteWorkout(req.params.id);
      if (!deleted) {
        return res.status(404).json({ message: "Workout not found" });
      }
      res.status(204).send();
    } catch (error) {
      res.status(500).json({ message: "Failed to delete workout" });
    }
  });

  // Client routes
  app.get("/api/clients", async (req, res) => {
    try {
      const clients = await storage.getAllClients();
      res.json(clients);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch clients" });
    }
  });

  app.get("/api/clients/:id", async (req, res) => {
    try {
      const client = await storage.getClient(req.params.id);
      if (!client) {
        return res.status(404).json({ message: "Client not found" });
      }
      res.json(client);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch client" });
    }
  });

  app.post("/api/clients", async (req, res) => {
    try {
      const validatedData = insertClientSchema.parse(req.body);
      const client = await storage.createClient(validatedData);
      res.status(201).json(client);
    } catch (error) {
      if (error && typeof error === 'object' && 'name' in error && error.name === 'ZodError') {
        return res.status(400).json({ message: "Validation error", details: error });
      }
      res.status(500).json({ message: "Failed to create client" });
    }
  });

  app.put("/api/clients/:id", async (req, res) => {
    try {
      const validatedData = insertClientSchema.partial().parse(req.body);
      const client = await storage.updateClient(req.params.id, validatedData);
      if (!client) {
        return res.status(404).json({ message: "Client not found" });
      }
      res.json(client);
    } catch (error) {
      if (error && typeof error === 'object' && 'name' in error && error.name === 'ZodError') {
        return res.status(400).json({ message: "Validation error", details: error });
      }
      res.status(500).json({ message: "Failed to update client" });
    }
  });

  app.delete("/api/clients/:id", async (req, res) => {
    try {
      const deleted = await storage.deleteClient(req.params.id);
      if (!deleted) {
        return res.status(404).json({ message: "Client not found" });
      }
      res.status(204).send();
    } catch (error) {
      res.status(500).json({ message: "Failed to delete client" });
    }
  });

  // Coach profile routes
  app.get("/api/coach-profile", async (req, res) => {
    try {
      const profile = await storage.getDefaultCoachProfile();
      if (!profile) {
        return res.status(404).json({ message: "Coach profile not found" });
      }
      res.json(profile);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch coach profile" });
    }
  });

  app.get("/api/coach-profile/:id", async (req, res) => {
    try {
      const profile = await storage.getCoachProfile(req.params.id);
      if (!profile) {
        return res.status(404).json({ message: "Coach profile not found" });
      }
      res.json(profile);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch coach profile" });
    }
  });

  app.post("/api/coach-profile", async (req, res) => {
    try {
      const validatedData = insertCoachProfileSchema.parse(req.body);
      const profile = await storage.createCoachProfile(validatedData);
      res.status(201).json(profile);
    } catch (error) {
      if (error && typeof error === 'object' && 'name' in error && error.name === 'ZodError') {
        return res.status(400).json({ message: "Validation error", details: error });
      }
      res.status(500).json({ message: "Failed to create coach profile" });
    }
  });

  app.put("/api/coach-profile/:id", async (req, res) => {
    try {
      const validatedData = insertCoachProfileSchema.partial().parse(req.body);
      const profile = await storage.updateCoachProfile(req.params.id, validatedData);
      if (!profile) {
        return res.status(404).json({ message: "Coach profile not found" });
      }
      res.json(profile);
    } catch (error) {
      if (error && typeof error === 'object' && 'name' in error && error.name === 'ZodError') {
        return res.status(400).json({ message: "Validation error", details: error });
      }
      res.status(500).json({ message: "Failed to update coach profile" });
    }
  });

  const httpServer = createServer(app);

  return httpServer;
}

server/storage.ts.content:
import { 
  type Workout, 
  type InsertWorkout,
  type Client,
  type InsertClient,
  type CoachProfile,
  type InsertCoachProfile
} from "@shared/schema";
import { randomUUID } from "crypto";

// Storage interface for fitness coach application
export interface IStorage {
  // Workout operations
  getWorkout(id: string): Promise<Workout | undefined>;
  getAllWorkouts(): Promise<Workout[]>;
  createWorkout(workout: InsertWorkout): Promise<Workout>;
  updateWorkout(id: string, workout: Partial<InsertWorkout>): Promise<Workout | undefined>;
  deleteWorkout(id: string): Promise<boolean>;

  // Client operations
  getClient(id: string): Promise<Client | undefined>;
  getAllClients(): Promise<Client[]>;
  createClient(client: InsertClient): Promise<Client>;
  updateClient(id: string, client: Partial<InsertClient>): Promise<Client | undefined>;
  deleteClient(id: string): Promise<boolean>;

  // Coach profile operations
  getCoachProfile(id: string): Promise<CoachProfile | undefined>;
  getDefaultCoachProfile(): Promise<CoachProfile | undefined>;
  createCoachProfile(profile: InsertCoachProfile): Promise<CoachProfile>;
  updateCoachProfile(id: string, profile: Partial<InsertCoachProfile>): Promise<CoachProfile | undefined>;
}

export class MemStorage implements IStorage {
  private workouts: Map<string, Workout>;
  private clients: Map<string, Client>;
  private coachProfiles: Map<string, CoachProfile>;

  constructor() {
    this.workouts = new Map();
    this.clients = new Map();
    this.coachProfiles = new Map();
  }

  // Workout operations
  async getWorkout(id: string): Promise<Workout | undefined> {
    return this.workouts.get(id);
  }

  async getAllWorkouts(): Promise<Workout[]> {
    return Array.from(this.workouts.values());
  }

  async createWorkout(insertWorkout: InsertWorkout): Promise<Workout> {
    const id = randomUUID();
    const now = new Date();
    const workout: Workout = { 
      ...insertWorkout, 
      id, 
      createdAt: now,
      updatedAt: now
    };
    this.workouts.set(id, workout);
    return workout;
  }

  async updateWorkout(id: string, updateData: Partial<InsertWorkout>): Promise<Workout | undefined> {
    const existing = this.workouts.get(id);
    if (!existing) return undefined;

    const updated: Workout = {
      ...existing,
      ...updateData,
      id,
      updatedAt: new Date()
    };
    this.workouts.set(id, updated);
    return updated;
  }

  async deleteWorkout(id: string): Promise<boolean> {
    return this.workouts.delete(id);
  }

  // Client operations
  async getClient(id: string): Promise<Client | undefined> {
    return this.clients.get(id);
  }

  async getAllClients(): Promise<Client[]> {
    return Array.from(this.clients.values());
  }

  async createClient(insertClient: InsertClient): Promise<Client> {
    const id = randomUUID();
    const client: Client = { 
      ...insertClient, 
      id, 
      createdAt: new Date()
    };
    this.clients.set(id, client);
    return client;
  }

  async updateClient(id: string, updateData: Partial<InsertClient>): Promise<Client | undefined> {
    const existing = this.clients.get(id);
    if (!existing) return undefined;

    const updated: Client = {
      ...existing,
      ...updateData,
      id
    };
    this.clients.set(id, updated);
    return updated;
  }

  async deleteClient(id: string): Promise<boolean> {
    return this.clients.delete(id);
  }

  // Coach profile operations
  async getCoachProfile(id: string): Promise<CoachProfile | undefined> {
    return this.coachProfiles.get(id);
  }

  async getDefaultCoachProfile(): Promise<CoachProfile | undefined> {
    // Restituisce il primo profilo coach disponibile o undefined
    const profiles = Array.from(this.coachProfiles.values());
    return profiles.length > 0 ? profiles[0] : undefined;
  }

  async createCoachProfile(insertProfile: InsertCoachProfile): Promise<CoachProfile> {
    const id = randomUUID();
    const profile: CoachProfile = { ...insertProfile, id };
    this.coachProfiles.set(id, profile);
    return profile;
  }

  async updateCoachProfile(id: string, updateData: Partial<InsertCoachProfile>): Promise<CoachProfile | undefined> {
    const existing = this.coachProfiles.get(id);
    if (!existing) return undefined;

    const updated: CoachProfile = {
      ...existing,
      ...updateData,
      id
    };
    this.coachProfiles.set(id, updated);
    return updated;
  }
}

export const storage = new MemStorage();

