}

export function useCreateCoachProfile() {
  const queryClient = useQueryClient();

  return useMutation({
    mutationFn: (profile: InsertCoachProfile) => dbOps.createCoachProfile(profile),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['coach-profile'] });
    },
