  return useMutation({
    mutationFn: ({ id, updates }: { id: string; updates: Partial<InsertCoachProfile> }) =>
      dbOps.updateCoachProfile(id, updates),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['coach-profile'] });
    },
  });
}

