import { useState, useEffect } from 'react';
import { Save, Upload, Download, Trash2, User, Camera, Instagram, Facebook, Globe, FileText, Folder, Palette, Eye, EyeOff, Type } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Switch } from '@/components/ui/switch';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from '@/components/ui/form';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { useCoachProfile, useCreateCoachProfile, useUpdateCoachProfile } from '@/hooks/use-clients';
import { BackupManager } from '@/lib/backup';
import { dbOps } from '@/lib/database';
import { useToast } from '@/hooks/use-toast';
import { insertCoachProfileSchema, type InsertCoachProfile } from '@shared/schema';
import { useRef } from 'react';
import { useAuth } from '@/hooks/use-auth';
import { processImageForUpload } from '@/lib/image-utils';

export default function Settings() {
  const { data: coachProfile, isLoading } = useCoachProfile();
  const createCoachProfile = useCreateCoachProfile();
  const updateCoachProfile = useUpdateCoachProfile();
  const { toast } = useToast();
  const { user } = useAuth();
  const fileInputRef = useRef<HTMLInputElement>(null);
  const logoInputRef = useRef<HTMLInputElement>(null);
  const [backupStats, setBackupStats] = useState<{
    workoutsCount: number;
    clientsCount: number;
    lastBackup?: Date;
  } | null>(null);

  const form = useForm<InsertCoachProfile>({
    resolver: zodResolver(insertCoachProfileSchema),
    defaultValues: {
      name: coachProfile?.name || '',
      email: coachProfile?.email || '',
      phone: coachProfile?.phone || '',
      bio: coachProfile?.bio || '',
      logo: coachProfile?.logo || '',
      instagram: coachProfile?.instagram || '',
      facebook: coachProfile?.facebook || '',
      website: coachProfile?.website || '',
      exportPath: coachProfile?.exportPath || '',
      pdfLineColor: coachProfile?.pdfLineColor || '#000000',
      pdfTextColor: coachProfile?.pdfTextColor || '#4F46E5',
      showWatermark: coachProfile?.showWatermark ?? true,
      useWorkoutNameAsTitle: coachProfile?.useWorkoutNameAsTitle ?? false
    }
  });

  // Update form when coach profile loads
  useEffect(() => {
    if (coachProfile) {
      form.reset({
        name: coachProfile.name,
        email: coachProfile.email || '',
        phone: coachProfile.phone || '',
        bio: coachProfile.bio || '',
        logo: coachProfile.logo || '',
        instagram: coachProfile.instagram || '',
        facebook: coachProfile.facebook || '',
        website: coachProfile.website || '',
        exportPath: coachProfile.exportPath || '',
        pdfLineColor: coachProfile.pdfLineColor || '#000000',
        pdfTextColor: coachProfile.pdfTextColor || '#4F46E5',
        showWatermark: coachProfile.showWatermark ?? true,
        useWorkoutNameAsTitle: coachProfile.useWorkoutNameAsTitle ?? false
      });
    }
  }, [coachProfile, form]);

  // Load backup stats on mount
  useEffect(() => {
    BackupManager.getBackupStats().then(setBackupStats);
  }, []);

  const handleSaveProfile = async (data: InsertCoachProfile) => {
    try {
      if (coachProfile) {
        // Aggiorna direttamente nel database locale
        await dbOps.updateCoachProfile(coachProfile.id, data);
        toast({
          title: "Profilo aggiornato",
          description: "Le modifiche sono state salvate e rimarranno visibili"
        });
      } else {
        // Crea nuovo profilo nel database locale
        await dbOps.createCoachProfile(data);
        toast({
          title: "Profilo creato",
          description: "Il profilo è stato salvato e rimarrà visibile"
        });
      }

      // Ricarica la pagina automaticamente dopo il salvataggio
      window.location.reload();
    } catch (error) {
      console.error('Errore nel salvataggio:', error);
      toast({
        title: "Errore",
        description: "Impossibile salvare il profilo",
        variant: "destructive"
      });
    }
  };

  const handleLogoUpload = async (file: File) => {
    try {
      const logoBase64 = await processImageForUpload(file, 400, 400);
      form.setValue('logo', logoBase64);
      toast({
        title: "Logo caricato",
        description: "Il logo è stato caricato con successo"
      });
    } catch (error) {
      toast({
        title: "Errore",
        description: error instanceof Error ? error.message : "Impossibile caricare il logo",
        variant: "destructive"
      });
    }
  };

  const handleBackup = async () => {
    try {
      await BackupManager.exportToJSON();
      BackupManager.setLastBackupDate();

      // Update backup stats
      const stats = await BackupManager.getBackupStats();
      setBackupStats(stats);

      toast({
        title: "Backup completato",
        description: "I dati sono stati esportati con successo"
      });
    } catch (error) {
      toast({
        title: "Errore",
        description: "Impossibile completare il backup",
        variant: "destructive"
      });
    }
  };


  const handleImport = async (file: File) => {
    try {
      await BackupManager.importFromJSON(file);

      // Update backup stats
      const stats = await BackupManager.getBackupStats();
      setBackupStats(stats);

      toast({
        title: "Importazione completata",
        description: "I dati sono stati importati con successo"
      });

      // Refresh the page to show imported data
      setTimeout(() => window.location.reload(), 1000);
    } catch (error) {
      toast({
        title: "Errore",
        description: error instanceof Error ? error.message : "Errore durante l'importazione",
        variant: "destructive"
      });
    }
  };

  const handleClearData = async () => {
    if (window.confirm('Sei sicuro di voler cancellare tutti i dati? Questa azione non può essere annullata.')) {
      try {
        await dbOps.clearAllData();

        // Update backup stats
        const stats = await BackupManager.getBackupStats();
        setBackupStats(stats);

        toast({
          title: "Dati cancellati",
          description: "Tutti i dati sono stati rimossi"
        });

        // Refresh the page
        setTimeout(() => window.location.reload(), 1000);
      } catch (error) {
        toast({
          title: "Errore",
          description: "Impossibile cancellare i dati",
          variant: "destructive"
        });
      }
    }
  };

  return (
    <main className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8 py-8 pb-mobile-nav">
      <div className="mb-8 md:mb-12">
        <h1 className="text-3xl md:text-4xl font-bold text-gray-900 dark:text-white mb-4">
          Impostazioni
        </h1>
        <p className="text-gray-600 dark:text-gray-400 text-lg md:text-xl">
          Configura il tuo profilo coach e le preferenze dell'app
        </p>
      </div>

      <div className="space-y-8 lg:space-y-12">
        {/* Coach Profile */}
        <Card className="glass-effect rounded-2xl animate-fade-in">
          <CardHeader>
            <CardTitle className="flex items-center">
              <User className="mr-2 text-indigo-500" size={20} />
              Profilo Coach
            </CardTitle>
          </CardHeader>
          <CardContent>
            <Form {...form}>
              <form onSubmit={form.handleSubmit(handleSaveProfile)} className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <FormField
                    control={form.control}
                    name="name"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Nome</FormLabel>
                        <FormControl>
                          <Input placeholder="Il tuo nome" {...field} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="email"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Email (opzionale)</FormLabel>
                        <FormControl>
                          <Input type="email" placeholder="email@esempio.com" {...field} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                <FormField
                  control={form.control}
                  name="phone"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Telefono (opzionale)</FormLabel>
                      <FormControl>
                        <Input placeholder="+39 123 456 7890" {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                {/* Logo Upload */}
                <div className="space-y-2">
                  <label className="text-sm font-medium text-gray-700 dark:text-gray-200">
                    Logo (opzionale)
                  </label>
                  <div className="flex items-center gap-4">
                    {form.watch('logo') && (
                      <div className="relative">
                        <img 
                          src={form.watch('logo')} 
                          alt="Logo" 
                          className="w-16 h-16 object-cover rounded-lg border border-gray-200 dark:border-gray-700"
                        />
                        <Button
                          type="button"
                          variant="destructive"
                          size="sm"
                          className="absolute -top-2 -right-2 h-6 w-6 rounded-full p-0"
                          onClick={() => form.setValue('logo', '')}
                        >
                          ×
                        </Button>
                      </div>
                    )}
                    <Button
                      type="button"
                      variant="outline"
                      onClick={() => logoInputRef.current?.click()}
                      className="flex items-center gap-2"
                    >
                      <Camera size={16} />
                      {form.watch('logo') ? 'Cambia Logo' : 'Carica Logo'}
                    </Button>
                  </div>
                </div>

                {/* Social Media Section */}
                <div className="space-y-4">
                  <h3 className="text-lg font-medium text-gray-900 dark:text-white flex items-center gap-2">
                    <Globe size={20} className="text-indigo-500" />
                    Social Media & Contatti
                  </h3>

                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <FormField
                      control={form.control}
                      name="instagram"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel className="flex items-center gap-2">
                            <Instagram size={16} className="text-pink-500" />
                            Instagram (opzionale)
                          </FormLabel>
                          <FormControl>
                            <Input placeholder="@nomeutente o URL completo" {...field} />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="facebook"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel className="flex items-center gap-2">
                            <Facebook size={16} className="text-blue-600" />
                            Facebook (opzionale)
                          </FormLabel>
                          <FormControl>
                            <Input placeholder="Nome pagina o URL completo" {...field} />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  <FormField
                    control={form.control}
                    name="website"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel className="flex items-center gap-2">
                          <Globe size={16} className="text-green-600" />
                          Sito Web (opzionale)
                        </FormLabel>
                        <FormControl>
                          <Input placeholder="https://www.tuosito.com" {...field} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                {/* Export Settings */}
                <div className="space-y-4">
                  <h3 className="text-lg font-medium text-gray-900 dark:text-white flex items-center gap-2">
                    <FileText size={20} className="text-purple-500" />
                    Impostazioni Export
                  </h3>

                  <FormField
                    control={form.control}
                    name="exportPath"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel className="flex items-center gap-2">
                          <Folder size={16} className="text-purple-500" />
                          Cartella Export PDF (opzionale)
                        </FormLabel>
                        <FormControl>
                          <Input 
                            placeholder="(default: Downloads)" 
                            {...field} 
                          />
                        </FormControl>
                        <FormMessage />
                        <p className="text-xs text-gray-500 dark:text-gray-400">
                          Lascia vuoto per usare la cartella Downloads di default
                        </p>
                      </FormItem>
                    )}
                  />

                  {/* Personalizzazione PDF */}
                  <div className="pt-4 border-t border-gray-200 dark:border-gray-700">
                    <h4 className="text-md font-medium text-gray-900 dark:text-white mb-4 flex items-center gap-2">
                      <Palette size={18} className="text-emerald-500" />
                      Personalizzazione PDF
                    </h4>

                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                      <FormField
                        control={form.control}
                        name="pdfLineColor"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel className="flex items-center gap-2">
                              <Palette size={14} className="text-emerald-500" />
                              Colore Linee PDF
                            </FormLabel>
                            <FormControl>
                              <div className="flex items-center gap-2">
                                <Input 
                                  type="color" 
                                  {...field} 
                                  className="w-16 h-10 p-1 rounded border cursor-pointer"
                                />
                                <Input 
                                  type="text"
                                  placeholder="#000000"
                                  {...field}
                                  className="flex-1"
                                />
                              </div>
                            </FormControl>
                            <FormMessage />
                            <p className="text-xs text-gray-500 dark:text-gray-400">
                              Colore delle linee e dei bordi nei PDF esportati
                            </p>
                          </FormItem>
                        )}
                      />

                      <FormField
                        control={form.control}
                        name="pdfTextColor"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel className="flex items-center gap-2">
                              <Type size={14} className="text-emerald-500" />
                              Colore Titoli PDF
                            </FormLabel>
                            <FormControl>
                              <div className="flex items-center gap-2">
                                <Input 
                                  type="color" 
                                  {...field} 
                                  className="w-16 h-10 p-1 rounded border cursor-pointer"
                                />
                                <Input 
                                  type="text"
                                  placeholder="#4F46E5"
                                  {...field}
                                  className="flex-1"
                                />
                              </div>
                            </FormControl>
                            <FormMessage />
                            <p className="text-xs text-gray-500 dark:text-gray-400">
                              Colore dei titoli delle sezioni nei PDF (DESCRIZIONE, PROGRESSIONE SETTIMANALE, ecc.)
                            </p>
                          </FormItem>
                        )}
                      />
                    </div>

                    <div className="mt-4 space-y-4">
                      <FormField
                        control={form.control}
                        name="useWorkoutNameAsTitle"
                        render={({ field }) => (
                          <FormItem className="flex flex-row items-center justify-between rounded-lg border p-4 space-y-0">
                            <div className="space-y-0.5">
                              <FormLabel className="flex items-center gap-2">
                                <Type size={14} className="text-purple-500" />
                                Usa Nome Scheda come Titolo
                              </FormLabel>
                              <p className="text-xs text-gray-500 dark:text-gray-400">
                                Se attivato, usa il nome della scheda al posto di "SCHEDA DI ALLENAMENTO" nei PDF
                              </p>
                            </div>
                            <FormControl>
                              <Switch
                                checked={field.value}
                                onCheckedChange={field.onChange}
                              />
                            </FormControl>
                          </FormItem>
                        )}
                      />

                      <FormField
                        control={form.control}
                        name="showWatermark"
                        render={({ field }) => (
                          <FormItem className="flex flex-row items-center justify-between rounded-lg border p-4 space-y-0">
                            <div className="space-y-0.5">
                              <FormLabel className="flex items-center gap-2">
                                {field.value ? <Eye size={14} className="text-blue-500" /> : <EyeOff size={14} className="text-gray-400" />}
                                Scritta "Generato con EasyWorkout Planner"
                              </FormLabel>
                              <p className="text-xs text-gray-500 dark:text-gray-400">
                                Mostra/nascondi la piccola scritta in basso nei PDF
                              </p>
                            </div>
                            <FormControl>
                              <Switch
                                checked={field.value}
                                onCheckedChange={field.onChange}
                              />
                            </FormControl>
                          </FormItem>
                        )}
                      />
                    </div>
                  </div>
                </div>

                <FormField
                  control={form.control}
                  name="bio"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Biografia (opzionale)</FormLabel>
                      <FormControl>
                        <Textarea 
                          placeholder="Scrivi qualcosa su di te..."
                          rows={4}
                          {...field}
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <Button 
                  type="submit" 
                  disabled={createCoachProfile.isPending || updateCoachProfile.isPending}
                  className="w-full md:w-auto bg-gradient-primary hover:opacity-90 transition-opacity"
                >
                  <Save className="mr-2" size={16} />
                  Salva Profilo
                </Button>
              </form>
            </Form>
          </CardContent>
        </Card>

        {/* Data Management */}
        <Card className="glass-effect rounded-2xl animate-fade-in">
          <CardHeader>
            <CardTitle>Gestione Dati</CardTitle>
          </CardHeader>
          <CardContent className="space-y-6">
            {/* Backup Stats */}
            {backupStats && (
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4 p-4 bg-gradient-to-r from-blue-50 to-indigo-50 dark:from-blue-900/20 dark:to-indigo-900/20 rounded-xl">
                <div className="text-center">
                  <p className="text-2xl font-bold text-gray-900 dark:text-white">
                    {backupStats.workoutsCount}
                  </p>
                  <p className="text-sm text-gray-600 dark:text-gray-400">Schede</p>
                </div>
                <div className="text-center">
                  <p className="text-2xl font-bold text-gray-900 dark:text-white">
                    {backupStats.clientsCount}
                  </p>
                  <p className="text-sm text-gray-600 dark:text-gray-400">Clienti</p>
                </div>
                <div className="text-center">
                  <p className="text-sm font-medium text-gray-900 dark:text-white">
                    {backupStats.lastBackup 
                      ? `Ultimo backup: ${backupStats.lastBackup.toLocaleDateString('it-IT')}`
                      : 'Nessun backup'
                    }
                  </p>
                </div>
              </div>
            )}

            {/* Azioni Backup/Cloud */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <Button
                onClick={handleBackup}
                className="h-auto p-6 bg-gradient-to-r from-emerald-50 to-teal-50 dark:from-emerald-900/30 dark:to-teal-900/30 border border-emerald-200 dark:border-emerald-700 hover:from-emerald-100 hover:to-teal-100 dark:hover:from-emerald-800/40 dark:hover:to-teal-800/40 text-left justify-start min-h-[100px] w-full"
                variant="ghost"
              >
                <div className="flex items-center w-full">
                  <Download className="text-emerald-500 mr-4" size={24} />
                  <div>
                    <p className="font-medium text-gray-900 dark:text-white text-base">
                      Esporta Backup
                    </p>
                    <p className="text-xs text-gray-500 dark:text-gray-400">
                      Salva tutti i dati
                    </p>
                  </div>
                </div>
              </Button>

              <Button
                onClick={() => fileInputRef.current?.click()}
                className="h-auto p-6 bg-gradient-to-r from-indigo-50 to-purple-50 dark:from-indigo-900/30 dark:to-purple-900/30 border border-indigo-200 dark:border-indigo-700 hover:from-indigo-100 hover:to-purple-100 dark:hover:from-indigo-800/40 dark:hover:to-purple-800/40 text-left justify-start min-h-[100px] w-full"
                variant="ghost"
              >
                <div className="flex items-center w-full">
                  <Upload className="text-indigo-500 mr-4" size={24} />
                  <div>
                    <p className="font-medium text-gray-900 dark:text-white text-base">
                      Importa Backup
                    </p>
                    <p className="text-xs text-gray-500 dark:text-gray-400">
                      Ripristina da file
                    </p>
                  </div>
                </div>
              </Button>

              {/* Carica in Cloud (sovrascrive) */}
              <Button
                onClick={async () => {
                  if (!user) {
                    toast({ title: 'Serve un account', description: 'Crea o accedi per usare il cloud', variant: 'destructive' });
                    return;
                  }
                  if (!window.confirm('Il backup remoto verrà reimpiazzato con i dati attuali, è consigliato prima caricare i dati dal cloud. Procedere?')) return;
                  try {
                    await BackupManager.exportToSupabaseStorage();
                    const stats = await BackupManager.getBackupStats();
                    setBackupStats(stats);
                    toast({ title: 'Caricato in cloud', description: 'Backup remoto aggiornato.' });
                  } catch (error) {
                    toast({ title: 'Errore', description: 'Impossibile caricare in cloud', variant: 'destructive' });
                  }
                }}
                className="h-auto p-6 text-left justify-start rounded-xl relative bg-sky-100 dark:bg-sky-900/30 border-2 border-yellow-500 hover:bg-sky-200 dark:hover:bg-sky-800/40 focus:outline-none focus:ring-2 focus:ring-yellow-500 focus:ring-offset-2 dark:focus:ring-offset-gray-900 min-h-[100px] w-full"
                variant="ghost"
              >
                <div className="flex items-center w-full">
                  <Upload className="text-sky-700 dark:text-sky-300 mr-4" size={24} />
                  <div>
                    <p className="font-semibold text-gray-900 dark:text-white text-base">
                      Carica in Cloud
                    </p>
                    <p className="text-xs text-gray-700 dark:text-gray-300">
                      Sostituisci i dati nel cloud
                    </p>
                  </div>
                </div>
              </Button>

              {/* Carica dal Cloud (sostituisce) */}
              <Button
                onClick={async () => {
                  if (!user) {
                    toast({ title: 'Serve un account', description: 'Crea un account o accedi per usare il cloud', variant: 'destructive' });
                    return;
                  }
                  if (!window.confirm('I dati locali verranno completamente sostituiti con quelli dal cloud. Procedere?')) return;
                  try {
                    await BackupManager.mergeFromSupabaseStorage();
                    const stats = await BackupManager.getBackupStats();
                    setBackupStats(stats);
                    toast({ title: 'Dati sostituiti', description: 'Dati locali sostituiti con quelli del cloud.' });
                    setTimeout(() => window.location.reload(), 500);
                  } catch (error) {
                    toast({ title: 'Errore', description: 'Impossibile caricare dal cloud', variant: 'destructive' });
                  }
                }}
                className="h-auto p-6 text-left justify-start rounded-xl relative bg-sky-100 dark:bg-sky-900/30 border-2 border-yellow-500 hover:bg-sky-200 dark:hover:bg-sky-800/40 focus:outline-none focus:ring-2 focus:ring-yellow-500 focus:ring-offset-2 dark:focus:ring-offset-gray-900 min-h-[100px] w-full"
                variant="ghost"
              >
                <div className="flex items-center w-full">
                  <Download className="text-sky-700 dark:text-sky-300 mr-4" size={24} />
                  <div>
                    <p className="font-semibold text-gray-900 dark:text-white text-base">
                      Carica dal Cloud
                    </p>
                    <p className="text-xs text-gray-700 dark:text-gray-300">
                      Sostituisci dati locali con quelli del cloud
                    </p>
                  </div>
                </div>
              </Button>

              <Button
                onClick={handleClearData}
                className="h-auto p-6 bg-gradient-to-r from-red-50 to-pink-50 dark:from-red-900/30 dark:to-pink-900/30 border border-red-200 dark:border-red-700 hover:from-red-100 hover:to-pink-100 dark:hover:from-red-800/40 dark:hover:to-pink-800/40 text-left justify-start min-h-[100px] w-full"
                variant="ghost"
              >
                <div className="flex items-center w-full">
                  <Trash2 className="text-red-500 mr-4" size={24} />
                  <div>
                    <p className="font-medium text-gray-900 dark:text-white text-base">
                      Cancella Tutto
                    </p>
                    <p className="text-xs text-gray-500 dark:text-gray-400">
                      Rimuovi tutti i dati
                    </p>
                  </div>
                </div>
              </Button>
            </div>

            {/* Pulsanti cloud espliciti: nessun salvataggio automatico */}
          </CardContent>
        </Card>
      </div>

      {/* Hidden file inputs */}
      <input
        ref={fileInputRef}
        type="file"
        accept=".json"
        onChange={(e) => {
          const file = e.target.files?.[0];
          if (file) {
            handleImport(file);
          }
        }}
        className="hidden"
      />

      <input
        ref={logoInputRef}
        type="file"
        accept="image/*"
        onChange={(e) => {
          const file = e.target.files?.[0];
          if (file) {
            handleLogoUpload(file);
          }
        }}
        className="hidden"
      />
    </main>
  );
}

client/src/pages/workout-detail.tsx.content:
import { useParams, useLocation, Link } from 'wouter';
import { useState } from 'react';
import { ArrowLeft, Edit, Copy, FileText, Save, Plus, Calendar } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { useWorkout, useUpdateWorkout } from '@/hooks/use-workouts';
import { useCoachProfile } from '@/hooks/use-clients';
import { useToast } from '@/hooks/use-toast';
import { pdfGenerator } from '@/lib/pdf-generator';
import { formatDistanceToNow } from 'date-fns';
import { it } from 'date-fns/locale';
import type { Workout, Day, Exercise } from '@shared/schema';

export default function WorkoutDetail() {
  const params = useParams();
  const workoutId = params.id as string;
  const [, setLocation] = useLocation();
  const { data: workout, isLoading } = useWorkout(workoutId);
  const { data: coachProfile } = useCoachProfile();
  const updateWorkout = useUpdateWorkout();
  const { toast } = useToast();
  const [isEditing, setIsEditing] = useState(false);
  const [editedWorkout, setEditedWorkout] = useState<Workout | null>(null);

  if (isLoading || !workout) {
    return (
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8 pb-mobile-nav">
        <div className="animate-pulse">
          <div className="h-8 bg-gray-200 dark:bg-gray-700 rounded w-1/2 mb-8"></div>
          <div className="space-y-4">
            {[1, 2, 3].map((i) => (
              <div key={i} className="h-40 bg-gray-200 dark:bg-gray-700 rounded-xl"></div>
            ))}
          </div>
        </div>
      </main>
    );
  }

  const handleEdit = () => {
    setEditedWorkout({ ...workout });
    setIsEditing(true);
  };

  const handleSave = async () => {
    if (!editedWorkout) return;

    try {
      await updateWorkout.mutateAsync({
        id: workout.id,
        updates: {
          ...editedWorkout,
          updatedAt: new Date()
        }
      });

      toast({
        title: "Scheda aggiornata",
        description: "Le modifiche sono state salvate con successo"
      });

      setIsEditing(false);
      setEditedWorkout(null);
    } catch (error) {
      toast({
        title: "Errore",
        description: "Impossibile salvare le modifiche",
        variant: "destructive"
      });
    }
  };

  const handleCancel = () => {
    setIsEditing(false);
    setEditedWorkout(null);
  };

  const handleExportPDF = async () => {
    try {
      const blob = await pdfGenerator.generateWorkoutPDF(workout, coachProfile);
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `scheda-${workout.clientName.replace(/\s+/g, '-').toLowerCase()}.pdf`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      URL.revokeObjectURL(url);

      // Update export count
      const currentCount = parseInt(localStorage.getItem('exportedPDFs') || '0');
      localStorage.setItem('exportedPDFs', (currentCount + 1).toString());

      toast({
        title: "PDF esportato",
        description: "Il PDF è stato scaricato con successo"
      });
    } catch (error) {
      toast({
        title: "Errore",
        description: "Impossibile esportare il PDF",
        variant: "destructive"
      });
    }
  };

  const currentWorkout = isEditing ? editedWorkout : workout;
  const timeAgo = formatDistanceToNow(workout.updatedAt, {
    addSuffix: true,
    locale: it
  });

  // Early return if currentWorkout is not available
  if (!currentWorkout) {
    return (
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8 pb-mobile-nav">
        <div className="animate-pulse">
          <div className="h-8 bg-gray-200 dark:bg-gray-700 rounded w-1/2 mb-8"></div>
          <div className="space-y-4">
            {[1, 2, 3].map((i) => (
              <div key={i} className="h-40 bg-gray-200 dark:bg-gray-700 rounded-xl"></div>
            ))}
          </div>
        </div>
      </main>
    );
  }

  return (
    <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8 pb-mobile-nav">
      {/* Header */}
      <div className="mb-8">
        {/* Desktop Header */}
        <div className="hidden md:flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <Link href="/workouts">
              <Button variant="ghost" size="sm" className="glass-effect">
                <ArrowLeft size={16} className="mr-2" />
                Indietro
              </Button>
            </Link>
            <div>
              <h1 className="text-3xl font-bold text-gray-900 dark:text-white">
                {currentWorkout.clientName}
              </h1>
              <p className="text-sm text-gray-500 dark:text-gray-400">
                {currentWorkout.workoutType} • {currentWorkout.duration} settimane • {timeAgo}
              </p>
            </div>
          </div>

          <div className="flex space-x-2">
            {isEditing ? (
              <>
                <Button onClick={handleSave} disabled={updateWorkout.isPending} className="bg-emerald-500 hover:bg-emerald-600">
                  <Save size={16} className="mr-2" />
                  Salva
                </Button>
                <Button onClick={handleCancel} variant="outline">
                  Annulla
                </Button>
              </>
            ) : (
              <>
                <Button onClick={handleEdit} className="bg-indigo-500 hover:bg-indigo-600">
                  <Edit size={16} className="mr-2" />
                  Modifica
                </Button>
                <Button onClick={handleExportPDF} className="bg-orange-500 hover:bg-orange-600">
                  <FileText size={16} className="mr-2" />
                  Esporta PDF
                </Button>
              </>
            )}
          </div>
        </div>

        {/* Mobile Header - Stack Layout */}
        <div className="md:hidden space-y-4">
          <div className="flex items-center justify-between">
            <Link href="/workouts">
              <Button variant="ghost" size="sm" className="glass-effect">
                <ArrowLeft size={16} className="mr-2" />
                Indietro
              </Button>
            </Link>
          </div>

          <div className="text-center">
            <h1 className="text-2xl font-bold text-gray-900 dark:text-white mb-2">
              {currentWorkout.clientName}
            </h1>
            <p className="text-sm text-gray-500 dark:text-gray-400 mb-4">
              {currentWorkout.workoutType} • {currentWorkout.duration} settimane
            </p>
            <p className="text-xs text-gray-400 dark:text-gray-500 mb-4">
              {timeAgo}
            </p>
          </div>

          <div className="flex flex-col space-y-2">
            {isEditing ? (
              <>
                <Button 
                  onClick={handleSave} 
                  disabled={updateWorkout.isPending} 
                  className="w-full bg-emerald-500 hover:bg-emerald-600"
                >
                  <Save size={16} className="mr-2" />
                  Salva Modifiche
                </Button>
                <Button onClick={handleCancel} variant="outline" className="w-full">
                  Annulla
                </Button>
              </>
            ) : (
              <>
                <Button onClick={handleEdit} className="w-full bg-indigo-500 hover:bg-indigo-600">
                  <Edit size={16} className="mr-2" />
                  Modifica Scheda
                </Button>
                <Button onClick={handleExportPDF} className="w-full bg-orange-500 hover:bg-orange-600">
                  <FileText size={16} className="mr-2" />
                  Esporta PDF
                </Button>
              </>
            )}
          </div>
        </div>
      </div>

      {/* Basic Info */}
      <Card className="glass-effect rounded-2xl mb-8 animate-fade-in">
        <CardHeader>
          <CardTitle>Informazioni Generali</CardTitle>
        </CardHeader>
        <CardContent className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
              Coach
            </label>
            {isEditing ? (
              <Input
                value={currentWorkout.coachName}
                onChange={(e) => setEditedWorkout({ ...currentWorkout, coachName: e.target.value })}
                className="glass-effect bg-white/50 dark:bg-gray-800/50"
              />
            ) : (
              <p className="text-gray-900 dark:text-white">{currentWorkout.coachName}</p>
            )}
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
              Tipo di Scheda
            </label>
            {isEditing ? (
              <Input
                value={currentWorkout.workoutType}
                onChange={(e) => setEditedWorkout({ ...currentWorkout, workoutType: e.target.value })}
                className="glass-effect bg-white/50 dark:bg-gray-800/50"
              />
            ) : (
              <p className="text-gray-900 dark:text-white">{currentWorkout.workoutType}</p>
            )}
          </div>

          <div className="md:col-span-2">
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
              Descrizione
            </label>
            {isEditing ? (
              <Textarea
                value={currentWorkout.description || ''}
                onChange={(e) => setEditedWorkout({ ...currentWorkout, description: e.target.value })}
                rows={3}
                className="glass-effect bg-white/50 dark:bg-gray-800/50"
              />
            ) : (
              <p className="text-gray-900 dark:text-white">{currentWorkout.description || 'Nessuna descrizione'}</p>
            )}
          </div>
        </CardContent>
      </Card>

      {/* Weekly Progression */}
      <Card className="glass-effect rounded-2xl mb-8 animate-fade-in">
        <CardHeader>
          <CardTitle className="flex items-center">
            <Calendar className="mr-2 text-indigo-500" size={20} />
            Progressione Settimanale
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-6">
            {(currentWorkout.weeks || []).map((week) => (
              <div key={week.id} className="border border-gray-200 dark:border-gray-600 rounded-xl p-4 bg-white/20 dark:bg-gray-800/20">
                <div className="flex items-center justify-between mb-4">
                  <h3 className="font-semibold text-lg text-gray-900 dark:text-white">
                    Settimana {week.number}
                  </h3>
                </div>

                {week.notes && (
                  <div className="mb-4 p-3 bg-blue-50 dark:bg-blue-900/20 rounded-lg">
                    <p className="text-sm text-blue-800 dark:text-blue-200">{week.notes}</p>
                  </div>
                )}

                <div className="space-y-4">
                  {(week.days || []).map((day) => (
                    <div key={day.id} className="border border-gray-100 dark:border-gray-700 rounded-lg p-3 bg-white/30 dark:bg-gray-900/30">
                      <h4 className="font-medium text-gray-900 dark:text-white mb-3">
                        {day.name}
                      </h4>

                      {day.notes && (
                        <div className="mb-3 p-2 bg-gray-50 dark:bg-gray-800/50 rounded-lg">
                          <p className="text-xs text-gray-600 dark:text-gray-400">{day.notes}</p>
                        </div>
                      )}

                      {(day.exercises || []).length > 0 ? (
                        <>
                          {/* Desktop Table View */}
                          <div className="hidden md:block overflow-x-auto">
                            <table className="w-full text-sm table-fixed">
                              <thead>
                                <tr className="border-b border-gray-200 dark:border-gray-600">
                                  <th className="text-left py-2 px-2 font-medium text-gray-700 dark:text-gray-300 w-1/2">Esercizio</th>
                                  <th className="text-left py-2 px-2 font-medium text-gray-700 dark:text-gray-300 w-16">Serie</th>
                                  <th className="text-left py-2 px-2 font-medium text-gray-700 dark:text-gray-300 w-20">Reps</th>
                                  <th className="text-left py-2 px-2 font-medium text-gray-700 dark:text-gray-300 w-24">Carico</th>
                                  <th className="text-left py-2 px-2 font-medium text-gray-700 dark:text-gray-300 w-28">Recupero</th>
                                </tr>
                              </thead>
                              <tbody>
                                {(day.exercises || []).map((exercise) => (
                                  <tr key={exercise.id} className="border-b border-gray-100 dark:border-gray-700">
                                    <td className="py-2 px-2 align-top">
                                      <div className="flex items-start space-x-2 min-w-0">
                                        {exercise.imageUrl && (
                                          <img 
                                            src={exercise.imageUrl} 
                                            alt="Exercise" 
                                            className="w-8 h-8 object-cover rounded"
                                          />
                                        )}
                                        <span className="text-gray-900 dark:text-white break-words whitespace-normal block min-w-0">{exercise.name}</span>
                                      </div>
                                    </td>
                                    <td className="py-2 px-2 text-gray-700 dark:text-gray-300 break-words whitespace-normal align-top">{exercise.sets}</td>
                                    <td className="py-2 px-2 text-gray-700 dark:text-gray-300 break-words whitespace-normal align-top">{exercise.reps}</td>
                                    <td className="py-2 px-2 text-gray-700 dark:text-gray-300 break-words whitespace-normal align-top">{exercise.load || '-'}</td>
                                    <td className="py-2 px-2 text-gray-700 dark:text-gray-300 break-words whitespace-normal align-top">{exercise.rest || '-'}</td>
                                  </tr>
                                ))}
                              </tbody>
                            </table>
                          </div>

                          {/* Mobile Card View */}
                          <div className="md:hidden space-y-3">
                            {(day.exercises || []).map((exercise) => (
                              <div key={exercise.id} className="bg-gray-50 dark:bg-gray-800/50 rounded-lg p-3 space-y-2">
                                <div className="flex items-center space-x-2">
                                  {exercise.imageUrl && (
                                    <img 
                                      src={exercise.imageUrl} 
                                      alt="Exercise" 
                                      className="w-10 h-10 object-cover rounded"
                                    />
                                  )}
                                  <h5 className="font-medium text-gray-900 dark:text-white text-sm">{exercise.name}</h5>
                                </div>
                                <div className="grid grid-cols-2 gap-2 text-xs">
                                  <div>
                                    <span className="font-medium text-gray-600 dark:text-gray-400">Serie:</span>
                                    <span className="ml-1 text-gray-900 dark:text-white">{exercise.sets}</span>
                                  </div>
                                  <div>
                                    <span className="font-medium text-gray-600 dark:text-gray-400">Reps:</span>
                                    <span className="ml-1 text-gray-900 dark:text-white">{exercise.reps}</span>
                                  </div>
                                  <div>
                                    <span className="font-medium text-gray-600 dark:text-gray-400">Carico:</span>
                                    <span className="ml-1 text-gray-900 dark:text-white">{exercise.load || '-'}</span>
                                  </div>
                                  <div>
                                    <span className="font-medium text-gray-600 dark:text-gray-400">Recupero:</span>
                                    <span className="ml-1 text-gray-900 dark:text-white">{exercise.rest || '-'}</span>
                                  </div>
                                </div>
                                {exercise.notes && (
                                  <div className="pt-1 border-t border-gray-200 dark:border-gray-600">
                                    <p className="text-xs text-gray-600 dark:text-gray-400">{exercise.notes}</p>
                                  </div>
                                )}
                              </div>
                            ))}
                          </div>
                        </>
                      ) : (
                        <p className="text-sm text-gray-500 dark:text-gray-400 text-center py-4">
                          Nessun esercizio per questo giorno
                        </p>
                      )}
                    </div>
                  ))}
                </div>
              </div>
            ))}
          </div>
        </CardContent>
      </Card>

      {/* Dietary Advice */}
      {currentWorkout.dietaryAdvice && (
        <Card className="glass-effect rounded-2xl animate-fade-in">
          <CardHeader>
            <CardTitle>Consigli Dietistici</CardTitle>
          </CardHeader>
          <CardContent>
            {isEditing ? (
              <Textarea
                value={currentWorkout.dietaryAdvice}
                onChange={(e) => setEditedWorkout({ ...currentWorkout, dietaryAdvice: e.target.value })}
                rows={4}
                className="glass-effect bg-white/50 dark:bg-gray-800/50"
              />
            ) : (
              <p className="text-gray-700 dark:text-gray-300 whitespace-pre-wrap">
                {currentWorkout.dietaryAdvice}
              </p>
            )}
          </CardContent>
        </Card>
      )}
    </main>
  );
}

client/src/pages/workouts.tsx.content:
import { useState } from 'react';
import { Plus, Search, Filter } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { WorkoutCard } from '@/components/workout-card';
import { WorkoutBuilder } from '@/components/workout-builder';
import { useWorkouts, useCreateWorkout } from '@/hooks/use-workouts';
import { useToast } from '@/hooks/use-toast';
import { workoutTypes, type Workout } from '@shared/schema';

export default function Workouts() {
  const { data: workouts = [], isLoading } = useWorkouts();
  const [searchTerm, setSearchTerm] = useState('');
  const [filterType, setFilterType] = useState<string>('all');
  const [showCreateDialog, setShowCreateDialog] = useState(false);
  const [editingWorkout, setEditingWorkout] = useState<Workout | null>(null);
  const createWorkout = useCreateWorkout();
  const { toast } = useToast();

  const filteredWorkouts = workouts.filter(workout => {
    const matchesSearch = 
      workout.clientName.toLowerCase().includes(searchTerm.toLowerCase()) ||
      (workout.level?.toLowerCase().includes(searchTerm.toLowerCase()) ?? false) || //DA DEBUGGARE
      workout.workoutType.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesType = filterType === 'all' || workout.workoutType === filterType;

    return matchesSearch && matchesType;
  });

  const handleEditWorkout = (workout: Workout) => {
    setEditingWorkout(workout);
  };

  const handleDuplicateWorkout = async (workout: Workout) => {
    try {
      const duplicatedWorkout = {
        ...workout,
        clientName: `${workout.clientName} (Copia)`,
        weeks: workout.weeks.map(week => ({
          ...week,
          id: crypto.randomUUID(),
          days: week.days.map(day => ({
            ...day,
            id: crypto.randomUUID(),
            exercises: day.exercises.map(exercise => ({
              ...exercise,
              id: crypto.randomUUID()
            }))
          }))
        }))
      };
      delete (duplicatedWorkout as any).id;
      delete (duplicatedWorkout as any).createdAt;
      delete (duplicatedWorkout as any).updatedAt;

      await createWorkout.mutateAsync(duplicatedWorkout);
      toast({
        title: "Scheda duplicata",
        description: "La scheda è stata copiata con successo"
      });
    } catch (error) {
      toast({
        title: "Errore",
        description: "Impossibile duplicare la scheda",
        variant: "destructive"
      });
    }
  };

  const handleCloseDialogs = () => {
    setShowCreateDialog(false);
    setEditingWorkout(null);
  };

  if (isLoading) {
    return (
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8 pb-mobile-nav">
        <div className="animate-pulse">
          <div className="h-8 bg-gray-200 dark:bg-gray-700 rounded w-1/3 mb-8"></div>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {[1, 2, 3, 4, 5, 6].map((i) => (
              <div key={i} className="h-32 bg-gray-200 dark:bg-gray-700 rounded-xl"></div>
            ))}
          </div>
        </div>
      </main>
    );
  }

  return (
    <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8 pb-mobile-nav">
      <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between mb-8 gap-4">
        <div>
          <h1 className="text-3xl md:text-4xl font-bold text-gray-900 dark:text-white mb-2">
            Schede di Allenamento
          </h1>
          <p className="text-gray-600 dark:text-gray-400 text-lg">
            Gestisci e organizza le tue schede workout
          </p>
        </div>
        <Dialog open={showCreateDialog} onOpenChange={setShowCreateDialog}>
          <DialogTrigger asChild>
            <Button className="bg-gradient-primary hover:opacity-90 transition-opacity h-12 px-6 text-lg font-semibold">
              <Plus className="mr-2" size={18} />
              Nuova Scheda
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
            <DialogHeader>
              <DialogTitle>Crea Nuova Scheda</DialogTitle>
            </DialogHeader>
            <WorkoutBuilder onSuccess={() => setShowCreateDialog(false)} />
          </DialogContent>
        </Dialog>
      </div>

      {/* Edit Dialog */}
      {editingWorkout && (
        <Dialog open={!!editingWorkout} onOpenChange={() => setEditingWorkout(null)}>
          <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
            <DialogHeader>
              <DialogTitle>Modifica Scheda - {editingWorkout.clientName}</DialogTitle>
            </DialogHeader>
            <WorkoutBuilder existingWorkout={editingWorkout} onSuccess={() => setEditingWorkout(null)} />
          </DialogContent>
        </Dialog>
      )}

      {/* Search and Filter */}
      <div className="flex flex-col md:flex-row gap-4 mb-8">
        <div className="relative flex-1">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={16} />
          <Input
            placeholder="Cerca per cliente, coach o tipo..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="pl-10 glass-effect bg-white/50 dark:bg-gray-800/50 backdrop-blur-sm"
          />
        </div>

        <div className="flex items-center gap-2">
          <Filter className="text-gray-400" size={16} />
          <Select value={filterType} onValueChange={setFilterType}>
            <SelectTrigger className="w-full md:w-48 glass-effect bg-white/50 dark:bg-gray-800/50 backdrop-blur-sm">
              <SelectValue placeholder="Filtra per tipo" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">Tutti i tipi</SelectItem>
              {workoutTypes.map((type) => (
                <SelectItem key={type} value={type}>
                  {type}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>
      </div>

      {/* Workouts Grid */}
      {filteredWorkouts.length > 0 ? (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredWorkouts.map((workout) => (
            <div key={workout.id} className="glass-effect rounded-2xl p-2">
              <WorkoutCard 
                workout={workout}
                onEdit={handleEditWorkout}
                onDuplicate={handleDuplicateWorkout}
              />
            </div>
          ))}
        </div>
      ) : (
        <div className="text-center py-16">
          <div className="w-24 h-24 bg-gradient-primary rounded-full flex items-center justify-center mx-auto mb-6 opacity-50">
            <Plus size={32} className="text-white" />
          </div>
          <h3 className="text-xl font-semibold text-gray-900 dark:text-white mb-2">
            {searchTerm || filterType !== 'all' ? 'Nessun risultato' : 'Nessuna scheda ancora'}
          </h3>
          <p className="text-gray-500 dark:text-gray-400 mb-6">
            {searchTerm || filterType !== 'all' 
              ? 'Prova a modificare i filtri di ricerca'
              : 'Crea la tua prima scheda di allenamento'
            }
          </p>
          <Button
            className="bg-gradient-primary hover:opacity-90 transition-opacity"
            onClick={() => setShowCreateDialog(true)}
          >
            <Plus className="mr-2" size={16} />
            Crea Nuova Scheda
          </Button>
        </div>
      )}
    </main>
  );
}

